import javax.swing.*;
import java.awt.*;
import java.awt.event.*;
public class MineSweeperGUI
{
   public static void main(String[] args)
   {
      JFrame frame = new JFrame ("My first GUI");
      frame.setSize(700, 700);
      frame.setLocation(200, 200);
      frame.setDefaultCloseOperation (JFrame.EXIT_ON_CLOSE);
      frame.setContentPane (new Panel00());
      frame.setVisible(true);
   } // main
} // Driver00
class Panel00 extends JPanel // step 2
{
   private int[][] board = new int[10][10];
   private JButton[][] guiBoard = new JButton[10][10];
   private int moveCount = 0;
   private int bombCount = 99999999;

   public Panel00() // step 4
   {
      setLayout(new GridLayout(guiBoard.length, guiBoard[0].length));
      for(int i = 0; i < guiBoard.length; i++){
         for(int j = 0; j < guiBoard[0].length; j++){
            guiBoard[i][j] = new JButton("");
            add(guiBoard[i][j]);
            guiBoard[i][j].addActionListener(new Move());
         }    
      } 
      
   }
            

   private class Move implements ActionListener{
      public void actionPerformed(ActionEvent e) {
         for (int row = 0; row < guiBoard.length; row++) {
            for (int col = 0; col < guiBoard[0].length; col++) {
               if (guiBoard[row][col] == e.getSource()){
                  if(moveCount<1){
                     bombCount = firstMove(row,col);
                     printBoard();
                     displayMove(row,col);
                     moveCount++;
                  }
                  else{
                     displayMove(row,col);
                     moveCount++;
                     if(gameWon(bombCount)||board[row][col]==-1){
                        System.exit(0);
                     }
                  }                   
               }
            }
         }
      }
      public boolean gameWon(int bombCount){
         int count = 0;
         for (int r = 0; r < board.length; r++){
            for(int c = 0; c < board[0].length; c++){
               if(guiBoard[r][c].getText().equals(board[r][c]+"")||guiBoard[r][c].getBackground().equals(new Color(84,54,0))){
                  count++;
               }        
            }
         }
         System.out.println(count);
         System.out.println(bombCount);
         return board.length*board[0].length - bombCount == count;
      }
   
   
      public void displayMove(int r, int c){
         if (board[r][c]==-1||!(guiBoard[r][c].getText().equals(""))||guiBoard[r][c].getBackground().equals(new Color(84,54,0))){
            return;
         }
         if(board[r][c]==0){
            guiBoard[r][c].setBackground(new Color(84, 54, 0));
         }else{
            guiBoard[r][c].setText(""+board[r][c]);
            guiBoard[r][c].setBackground(new Color(157, 233, 0));
         }
         if(board[r][c] != 0){
            return;
         }else{
            for (int r1 = r-1; r1 < r+2; r1++){
               for(int c1 = c-1; c1 < c+2; c1++){
                  if(r1 >= 0 && c1 >= 0 && r1 < board.length && c1 < board.length){
                     if((r1!=r||c1!=c)){   
                        displayMove(r1,c1);
                     }                    
                  }
               }    
            }                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
         }   
      }
   
      public int firstMove(int r, int c){
         int countBomb = generateBoard();
         while(board[r][c]!=0){
            countBomb = generateBoard();
         }
         return countBomb;
      }
      public void printBoard(){
         for (int r = 0; r < board.length; r++){
            for(int c = 0; c < board[0].length; c++){
               System.out.print(" | " + board[r][c]);
            }
            System.out.println(" |");
         }
      
      }
      public int generateBoard(){
         for (int r = 0; r < board.length; r++){
            for (int c = 0; c < board[0].length; c++){
               board[r][c]=0;
            }
         }
         int bombCount = 0;
         for (int r = 0; r < board.length; r++){
            for(int c = 0; c < board[0].length; c++){
               double x = Math.random();
               if(x < .1){
                  board[r][c] = -1;
                  bombCount++;
                  for (int r1 = r-1; r1 < r+2; r1++){
                     for(int c1 = c-1; c1 < c+2; c1++){
                        if(r1 >= 0 && c1 >= 0 && r1 < board.length && c1 < board.length){
                           if(board[r1][c1] != -1)
                              board[r1][c1]++;
                        }
                     }
                  }
               }
            } 
         }
         return bombCount;
      
              
            
      }
   }
   
}
